@alphabet = ('a'..'z').to_a

@foo_letters = ['j', 'g', 'x', 'r', 'm']
@bar_letters = @alphabet - @foo_letters

def text_preps(text)
	text_preps = 0
	text.scan(/\w+/).each do |w|
		if w.length == 3 and not w.index('b')
			# end in bar_letter ?		
			if @bar_letters.include? w[2]
				# puts w
				text_preps += 1
			end
		end
	end	
	text_preps
end

def verbs(text)
	verbs = 0
	subjunctive = 0
	text.scan(/\w+/).each do |w|
		if w.length >= 8 and @bar_letters.include? w[w.length - 1]
			verbs += 1
			
			if @bar_letters.include? w[0]
				subjunctive += 1
			end
		end
	end
	[verbs, subjunctive]
end

def compare_chars(a, b)
	s = "pmrwlhzskgcdqjxvftbn"
	
	if s.index(a) < s.index(b)
		-1
	elsif s.index(a) == s.index(b)
		0
	else
		1
	end
end

def compare(a, b)
	len1 = a.length 
	len2 = b.length 
	n = [len1, len2].min

	(0..n-1).each do |i|
		c1 = a[i]
		c2 = b[i]
	
		if c1 != c2
			return compare_chars(c1, c2)
		end
	end
	
	if a == b
		0
	elsif len1 > len2
		1
	else
		-1
	end
end

def vocabulary(text)
	text.scan(/\w+/).uniq.sort { |a, b| compare(a, b) }.join " "
end

